package com.yorbax.EMA.admin.activity;import androidx.appcompat.app.AppCompatActivity;import android.content.Intent;import android.os.Bundle;import com.yorbax.EMA.HomeActivity;import com.yorbax.EMA.R;public class ManageProfile extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_manage_profile);    }    @Override    public void onBackPressed() {        super.onBackPressed();        startActivity(new Intent(ManageProfile.this, HomeActivity.class));        finish();    }}