package com.yorbax.EMA.admin.adapter;import android.content.Context;import android.content.Intent;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import android.widget.Toast;import androidx.annotation.NonNull;import androidx.annotation.Nullable;import androidx.recyclerview.widget.RecyclerView;import com.google.firebase.database.DatabaseError;import com.google.firebase.database.DatabaseReference;import com.google.firebase.database.FirebaseDatabase;import com.yorbax.EMA.R;import com.yorbax.EMA.admin.activity.AddCourse;import com.yorbax.EMA.admin.model.CourseModel;import java.util.List;public class CourseAdapter extends RecyclerView.Adapter<CourseAdapter.ViewHolder> {    Context context;    List<CourseModel> list;    public CourseAdapter(Context context, List<CourseModel> list) {        this.context = context;        this.list = list;    }    @NonNull    @Override    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view= LayoutInflater.from(context).inflate(R.layout.row_lecture,parent,false);        return new ViewHolder(view);    }    @Override    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {        holder.courseName.setText("Course Name: "+list.get(position).courseName);        holder.creditHr.setText("Credit Hours: "+list.get(position).creditHr);        holder.ic_edit.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                context.startActivity(new Intent(context, AddCourse.class).putExtra("activity","update")                        .putExtra("updateData",list.get(position))                );            }        });        holder.ic_delete.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                FirebaseDatabase.getInstance().getReference().child("course").child(list.get(position).id).removeValue(new DatabaseReference.CompletionListener() {                    @Override                    public void onComplete(@Nullable DatabaseError error, @NonNull DatabaseReference ref) {                        Toast.makeText(context, "Deleted...!", Toast.LENGTH_SHORT).show();                    }                });            }        });    }    @Override    public int getItemCount() {        return list.size();    }    class ViewHolder extends RecyclerView.ViewHolder {        TextView courseName,creditHr;        ImageView ic_edit,ic_delete;        public ViewHolder(@NonNull View itemView) {            super(itemView);            courseName=itemView.findViewById(R.id.txt_name);            creditHr=itemView.findViewById(R.id.txt_email);            ic_delete=itemView.findViewById(R.id.ic_delete);            ic_edit=itemView.findViewById(R.id.ic_edit);        }    }}