package com.yorbax.EMA.lecturer.model;import java.io.Serializable;public class McqsDetail implements Serializable {    public String id;    public String mcqsQuestion;    public String option1;    public String option2;    public String option3;    public String option4;    public String correctOption;    public String questionId;    public McqsDetail() {    }    public McqsDetail(String id, String mcqsQuestion, String option1, String option2, String option3, String option4, String correctOption) {        this.id = id;        this.mcqsQuestion = mcqsQuestion;        this.option1 = option1;        this.option2 = option2;        this.option3 = option3;        this.option4 = option4;        this.correctOption = correctOption;    }}